import axios from "axios";
import React, { useState } from "react";
import BookList from "./BookList";
import AddBookForm from "./AddBookForm";

function Homepage() {
  const [user, setUser] = useState({
    roles: ["CREATOR"], // Replace with actual user roles from authentication
  });
  const [books, setBooks] = useState([]);

  const handleAddBook = (newBook) => {
    setBooks([...books, newBook]);
  };

  const handleDeleteBook = async (bookId) => {
    try {
      await axios.delete(`/books/delete/${bookId}`, {
        headers: { Authorization: localStorage.getItem("token") },
      });
      setBooks(books.filter((book) => book._id !== bookId));
    } catch (error) {
      console.error("Error deleting book:", error);
    }
  };

  return (
    <div>
      <h1>Library App</h1>
      <BookList user={user} onDelete={handleDeleteBook} />
      {user.roles.includes("CREATOR") && <AddBookForm onAdd={handleAddBook} />}
    </div>
  );
}

export default Homepage;
