// Routes.js
import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import BookList from "../components/BookList";
import LoginForm from "../components/LoginForm";
import AddBookForm from "../components/AddBookForm";
import SignupForm from "../components/SignupForm";
import HomePage from "../components/Homepage";
const PrivateRoute = ({ element, user, ...rest }) => {
  // Check if `location` is defined, otherwise use a default value
  const { location = { pathname: "/" } } = rest;

  return user ? (
    React.cloneElement(element, { user })
  ) : (
    <Navigate replace to="/login" state={{ from: location.pathname }} />
  );
};

const Library = ({ user }) => {
  return (
    <div>
      <h1>Library App</h1>
      <BookList user={user} />
      {user.roles.includes("CREATOR") && <AddBookForm />}
    </div>
  );
};

const AllRoutes = ({ setUser }) => {
  const user = null; // Replace with your actual user state

  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginForm setUser={setUser} />} />
        <Route path="/signup" element={<SignupForm setUser={setUser} />} />
        <Route
          path="/library"
          element={<PrivateRoute element={<Library />} user={user} />}
        />
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default AllRoutes;
