// SignupForm.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { API } from "../env";

const SignupForm = ({ setUser }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const navigate = useNavigate();

  const handleSignup = async () => {
    try {
      await axios.post(`${API}/users/register`, {
        username,
        password,
        roles: [...role],
      });
      // Automatically log in the user after signup
      const response = await axios.post(`${API}/users/login`, {
        username,
        password,
      });
      localStorage.setItem("token", response.data.token);
      // Fetch user information based on token and set user state
      // Replace the following line with actual user data fetching logic
      setUser({ username, roles: ["CREATOR"] });
      navigate("/library");
    } catch (error) {
      console.error("Error signing up:", error);
    }
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        width: "300px",
        margin: "auto",
        gap: "1rem",
      }}
    >
      <h2>Sign Up</h2>
      <label>
        Username:
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ marginLeft: "1rem" }}
        />
      </label>
      <label>
        Password:
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ marginLeft: "1rem" }}
        />
      </label>
      <label>
        Role:
        <select
          style={{ marginLeft: "1rem" }}
          value={role}
          onChange={(e) => setRole(e.target.value)}
        >
          <option value="VIEWER">Viewer</option>
          <option value="CREATOR">Creator</option>
          <option value="VIEW_ALL">VIEW_ALL</option>
        </select>
      </label>
      <button
        onClick={handleSignup}
        style={{ padding: "0px 5px", width: "4rem" }}
      >
        Sign Up
      </button>
    </div>
  );
};

export default SignupForm;
