// App.js
import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Redirect,
  Navigate,
} from "react-router-dom";
import BookList from "./components/BookList";
import AddBookForm from "./components/AddBookForm";
import LoginForm from "./components/LoginForm";
import SignupForm from "./components/SignupForm";

const App = () => {
  const [user, setUser] = useState(null); // Updated to null initially
  const [books, setBooks] = useState([]);

  const handleAddBook = (newBook) => {
    setBooks([...books, newBook]);
  };

  const handleDeleteBook = async (bookId) => {
    // Same delete logic as before
  };

  return (
    <Router>
      <div>
        <Routes>
          <Route path="/login" element={<LoginForm setUser={setUser} />} />
          <Route path="/signup" element={<SignupForm setUser={setUser} />} />
          <Route
            path="/library"
            element={
              <Library
                user={user}
                onDelete={handleDeleteBook}
                onAdd={handleAddBook}
              />
            }
          />
          <Route path="/" element={<Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
};

const Library = ({ user, onDelete, onAdd }) => {
  return (
    <>
      {user ? (
        <div>
          <h1>Library App</h1>
          <BookList user={user} onDelete={onDelete} />
          {user.roles.includes("CREATOR") && <AddBookForm onAdd={onAdd} />}
        </div>
      ) : (
        <Navigate to="/login" />
      )}
    </>
  );
};

export default App;
