// SignupForm.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const SignupForm = ({ setUser }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("VIEWER"); // Default role is set to VIEWER
  const navigate = useNavigate();

  const handleSignup = async () => {
    try {
      await axios.post("/users/register", {
        username,
        password,
        roles: [role],
      });
      const response = await axios.post("/users/login", { username, password });
      localStorage.setItem("token", response.data.token);
      setUser({ username, roles: response.data.roles }); // Assuming the server sends back user roles
      navigate("/library");
    } catch (error) {
      console.error("Error signing up:", error);
      // Handle signup error, show a message to the user, or redirect to an error page
    }
  };

  return (
    <div>
      <h2>Sign Up</h2>
      <label>
        Username:
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </label>
      <label>
        Password:
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </label>
      <label>
        Role:
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="VIEWER">Viewer</option>
          <option value="CREATOR">Creator</option>
          {/* Add more roles if needed */}
        </select>
      </label>
      <button onClick={handleSignup}>Sign Up</button>
    </div>
  );
};

export default SignupForm;
